import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

class DropDown extends React.Component {
    state = {
        planets: []
    }



componentDidMount() {
    let initialPlanets = [];
    fetch('https://www.thesportsdb.com/api/v1/json/1/eventsnextleague.php?id=4424')
        .then(response => {
            return response.json();
        }).then(data => {
        initialPlanets = data.results.map((planet) => {
            return planet
        });
        console.log(initialPlanets);
        this.setState({
            planets: initialPlanets,
        });
    });
}


render() {
    return (
                <Planet state={this.state}/>
    );
}
}
// after component is finished

export default DropDown;

ReactDOM.render(<PlanetSearch />, document.getElementById('react-search'));
