import React from 'react';

let values;

const EventsContainer = (props)=>{

    const eventList = props.eventList.map((event)=>{
        return (
      <li key={event.idEvent}>
              <span>{event.dateEvent}</span>
          <a href={`/${event.idEvent}`} >{event.strEvent} </a>


      </li>
        )
    })
    return(
        <div className="events-dump">
            <h1>These are the Upcoming Games</h1>
            <ul>
                {eventList}
            </ul>
        </div>
    )
}

class DropDown extends React.Component {

    constructor(){
        super();
        this.state = {
            options: []
        }  
    }

    componentDidMount(){
        this.fetchOptions()
    }

    fetchOptions(){
       fetch('https://www.thesportsdb.com/api/v1/json/1/eventsnextleague.php?id=4424')
            .then((res) => {
                return res.json();
            }).then((json) => {
                values = json;
                this.setState({options: values.values})
                console.log(values);
            });
    }
    render(){
        return <div className="drop-down">
            <select>
                { this.state.options.map((option, key) => <option key={key} >{option}</option>) }
            </select>
            </div>;
    }
}

export default DropDown;
