import React from 'react';

class DropDown extends React.Component {
    constructor() {
        super();

        this.state = {
            values: []
        }
    }

    componentDidMount() {
        let json = null;
       fetch('https://www.thesportsdb.com/api/v1/json/1/eventsnextleague.php?id=4424')
        .then((res) => {
            console.log('hi', res, res.json());
            this.setState({values: res.data});
            console.log('show me the money', this.state);
            // console.log(123, res);
            // console.log('help', res.json());
            // console.log(456, json);
        })
    }
    render(){
        return <div className="drop-down">
              <select>{
                 this.state.values.map((obj) => {
                     return <option value={obj.strEvent}>{obj.strEvent}</option>
                 })
              }</select>
            </div>;
    }
}

export default DropDown;
